configuration.decoder:
// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to string
var payloadStr = decodeToString(payload);

// decode payload to JSON
// var data = decodeToJson(payload);

var deviceName = 'Device A';
var deviceType = 'thermostat';
var customerName = 'Customer C';
var groupName = 'thermostat devices';
var manufacturer = 'Example corporation';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// Result object with device/asset attributes/telemetry data
var result = {
// Use deviceName and deviceType or assetName and assetType, but not both.
   deviceName: deviceName,
   deviceType: deviceType,
// assetName: assetName,
// assetType: assetType,
// customerName: customerName,
   groupName: groupName,
   attributes: {
       model: 'Model A',
       serialNumber: 'SN111',
       integrationName: metadata['integrationName'],
       manufacturer: manufacturer
   },
   telemetry: {
       temperature: 42,
       humidity: 80,
       rawData: payloadStr
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // covert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

return result;

configuration.tbelDecoder:
var p = decodeToJson(payload);
var t = metadata.topic;
var tmp = {};
var pld = {};
var attrs = {topic: t};
var tlmty = null;

var wrd = t.split('/');
var grp  = wrd.length > 3 ? wrd[wrd.length-3] : null;
var eui = wrd.length > 2 ? wrd[wrd.length-2]: null;

if (eui != null) {
    attrs.put("devEUI", eui.toUpperCase())
}

if ( p.devName != null ) {
    pld.put('assetName', 'backup ' + p.devName);
    attrs.put('devName', p.devName);
}

if ( p.appName != null ) {
    pld.put('assetType', 'TxRxV2');
    attrs.put('appName', p.appName);
}

if ( grp != null ) {
    pld.put('groupName', grp);
}

if ( p.appID != null ) {
    attrs.put('appID', p.appID);
}

// Fill up telemetry:
if (p.frmPayload != null) tmp.put('frmPayload', p.frmPayload);
if (p.fPort != null) tmp.put('fPort', p.fPort);
if (p.fCnt != null) tmp.put('fCnt', p.fCnt);
if (p.tx != null) tmp.put('tx', p.tx);
if (p.rx != null) tmp.put('rx', p.rx);

if (p.ts != null && tmp != null) {
    tlmty = [{ts: p.ts, values: tmp}];
} else if (tmp != null) {
    tlmty = tmp;
}

if (tlmty != null) {
    pld.put('telemetry', tlmty);
}

if ( attrs.size() > 0 ) {
    pld.put('attributes', attrs);
}


return pld;
