settings.rowStyleFunction:
const t = entity.lastActivityTime != null ? Number(entity
    .lastActivityTime) : 0;

if (!t) {
    return {
        background: ''
    }; // or a neutral color if you prefer
}

const diffS = (Date.now() - t) / 1000;

if (diffS < 60 * 60) {
    // < 1 hour -> green
    return {
        background: '#8AFF8A'
    };
} else if (diffS < 24 * 60 * 60) {
    // < 1 day -> orange
    return {
        background: '#FF8000'
    };
} else {
    // >= 1 day -> red
    return {
        background: '#FF8A8A'
    };
}

dataKeys.postFuncBody:
//return value;
if (!value) return 'Never';

const d = new Date(value);
const diffMs = Date.now() - value;
const diffMin = Math.floor(diffMs / 60000);
const diffHr  = Math.floor(diffMin / 60);
const diffDay = Math.floor(diffHr / 24);

let ago;
if (diffMin < 1) ago = 'just now';
else if (diffMin < 60) ago = diffMin + ' min ago';
else if (diffHr < 24) ago = diffHr + ' h ago';
else ago = diffDay + ' d ago';

return d.toLocaleString() + ' (' + ago + ')';
